CREATE TABLE User (                                    id VARCHAR(255),                                    username VARCHAR(255),                                    password VARCHAR(255),                                    email VARCHAR(255),                                    PRIMARY KEY (id));CREATE TABLE Passenger (                                         id VARCHAR(255),                                         name VARCHAR(255),                                         surname VARCHAR(255),                                         mail VARCHAR(255),                                         PIN VARCHAR(255),                                         DOB DATE,                                         PRIMARY KEY (id));CREATE TABLE Airplane (                                        id VARCHAR(255),                                        registration VARCHAR(255),                                        model VARCHAR(255),                                        first_seats INT,                                        business_seats INT,                                        economy_seats INT,                                        PRIMARY KEY (id));CREATE TABLE Airport (                                       id VARCHAR(255),                                       airport_name VARCHAR(255),                                       IATA CHAR(3),                                       PRIMARY KEY (id));CREATE TABLE Flight (                                      id VARCHAR(255),                                      flight_number VARCHAR(255),                                      from_id VARCHAR(255),                                      to_id VARCHAR(255),                                      airplane_id VARCHAR(255),                                      date DATE,                                      time TIME,                                      gate VARCHAR(255),                                      duration TIME,                                      PRIMARY KEY (id),                                      FOREIGN KEY (from_id) REFERENCES Airport (id),                                      FOREIGN KEY (to_id) REFERENCES Airport (id),                                      FOREIGN KEY (airplane_id) REFERENCES Airplane (id));CREATE TABLE Seat (                                    id VARCHAR(255),                                    flight_id VARCHAR(255),                                    seat_price DECIMAL(10, 2),                                    seat_class VARCHAR(30),                                    PRIMARY KEY (id),                                    FOREIGN KEY (flight_id) REFERENCES Flight (id));CREATE TABLE Ticket (                                      id VARCHAR(255),                                      passenger_id VARCHAR(255),                                      flight_id VARCHAR(255),                                      seat_id VARCHAR(255),                                      ticket_price DECIMAL(10, 2),                                      PRIMARY KEY (id),                                      FOREIGN KEY (passenger_id) REFERENCES Passenger (id),                                      FOREIGN KEY (flight_id) REFERENCES Flight (id),                                      FOREIGN KEY (seat_id) REFERENCES Seat (id));CREATE TABLE Purchase (                                        id VARCHAR(255),                                        user_id VARCHAR(255),                                        ticket_id VARCHAR(255),                                        payment_info VARCHAR(255),                                        payment_date DATE,                                        PRIMARY KEY (id),                                        FOREIGN KEY (user_id) REFERENCES User (id),                                        FOREIGN KEY (ticket_id) REFERENCES Ticket (id));